import React, { useContext } from 'react';
import { Card, CardImg, CardText, CardBody, CardTitle, CardSubtitle, Button, Row, Col } from 'reactstrap';

import { AppContext } from '../lib/AppContext';
import { getUniqueProducts } from '../lib/utils';

const ProductsList = ({ products }) => {
	const { globalStore, updateStore } = useContext(AppContext);

	const { user, cart } = globalStore;

	const addProductToCard = (product) => {
		const cartData = { ...product, userId: user._id };
		let updatedCart = [...cart];
		updatedCart.push(cartData);
		debugger;
		updateStore('cart', getUniqueProducts(updatedCart));
		localStorage.setItem('cart', JSON.stringify(cartData));
	};

	return (
		<Row>
			{products.map((product, i) => {
				return (
					<Col key={i}>
						<Card className='p-2'>
							<Row>
								<Col md={3}>
									<div className='product-image'>
										<CardImg
											top
											width='100%'
											height='100%'
											src={`http://localhost:1337${product.displayImage.url}`}
											alt='Card image cap'
										/>
									</div>
								</Col>
								<Col>
									<Row>
										<Col>
											<CardTitle>{product.name}</CardTitle>
										</Col>
									</Row>
									<Row>
										<Col>
											<CardSubtitle>{product.price}</CardSubtitle>
										</Col>
									</Row>
									<Row>
										<Col>
											<Button onClick={() => addProductToCard(product)}>Add To Cart</Button>
										</Col>
									</Row>
								</Col>
							</Row>
						</Card>
					</Col>
				);
			})}
		</Row>
	);
};

export default ProductsList;
