import React, { Fragment, useContext, useState, useEffect } from 'react';

import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import { Row, Col, Button, Label, Input } from 'reactstrap';

import { AppContext } from '../lib/AppContext';
import { removeProductFromCart } from '../lib/utils';

import gql from 'graphql-tag';
import { useMutation } from '@apollo/react-hooks';

const PLACE_ORDER = gql`
	mutation createOrder($type: createOrderInput!) {
		createOrder(input: $type) {
			order {
				id
			}
		}
	}
`;

const CartItems = () => {
	const { globalStore, updateStore } = useContext(AppContext);
	const [shippingAddress, setShippingAddress] = useState();
	const [createOrder] = useMutation(PLACE_ORDER);

	const { user, cart } = globalStore;

	const [succeeded, setSucceeded] = useState(false);
	const [error, setError] = useState(null);
	const [processing, setProcessing] = useState('');
	const [disabled, setDisabled] = useState(true);
	const [clientSecret, setClientSecret] = useState('');

	const stripe = useStripe();
	const elements = useElements();

	useEffect(() => {
		// Create PaymentIntent as soon as the page loads
		window
			.fetch('/api/stripe', {
				method: 'POST',
				headers: {
					'Content-Type': 'application/json',
				},
				body: JSON.stringify({ amount: 7997 }),
			})
			.then((res) => {
				return res.json();
			})
			.then((data) => {
				setClientSecret(data.clientSecret);
			});
	}, []);

	const cardStyle = {
		style: {
			base: {
				color: '#32325d',
				fontFamily: 'Arial, sans-serif',
				fontSmoothing: 'antialiased',
				fontSize: '16px',
				'::placeholder': {
					color: '#32325d',
				},
			},
			invalid: {
				color: '#fa755a',
				iconColor: '#fa755a',
			},
		},
	};
	const handleChange = async (event) => {
		// Listen for changes in the CardElement
		// and display any errors as the customer types their card details
		setDisabled(event.empty);
		setError(event.error ? event.error.message : '');
	};

	const totalPrice = cart.reduce((a, c) => {
		return a + c.price;
	}, 0);

	const placeOrder = async () => {
		if (!shippingAddress) {
			return false;
		}

		const payload = await stripe.confirmCardPayment(clientSecret, {
			payment_method: {
				card: elements.getElement(CardElement),
				billing_details: {
					address: {
						city: null,
						country: null,
						line1: null,
						line2: null,
						postal_code: '18888',
						state: null,
					},
					email: 'jenny@example.com',
					name: null,
					phone: '+15555555555',
				},
			},
		});

		const productIds = cart.reduce((a, c) => {
			a.push(c.id);
			return a;
		}, []);
		createOrder({
			variables: {
				type: { data: { user: user._id, products: productIds, shippingAddress, amount: totalPrice } },
			},
		});
		updateStore('cart', []);
		localStorage.setItem('cart', JSON.stringify([]));
	};

	const removeItem = (cart, id) => {
		cart = removeProductFromCart(cart, id);
		updateStore('cart', cart);
		localStorage.setItem('cart', JSON.stringify(cart));
	};

	return (
		<Fragment>
			{cart.length > 0 && (
				<Row>
					<Col>
						{cart.map((product, i) => {
							return (
								<div key={i} className='transparent-container mb-2'>
									<Row>
										<Col md={3}>
											<div className='product-image'>
												<img
													top
													width='100%'
													height='100%'
													src={`${product.displayImage}`}
													alt='Card image cap'
												/>
											</div>
										</Col>
										<Col>
											<h4>{product.name}</h4>
											<p>Rs. {product.price}</p>
											<Button onClick={() => removeItem(cart, product.id)}>
												Remove From Cart
											</Button>
										</Col>
									</Row>
									<Row>
										<Col>
											<p className='mt-4'>{product.description}</p>
										</Col>
									</Row>
								</div>
							);
						})}
					</Col>
					<Col>
						<div className='transparent-container'>
							<p>Total Price: Rs. {totalPrice}</p>
							{user ? (
								<Fragment>
									<Label>Shipping Address:</Label>
									<Input
										type='textarea'
										name='text'
										id='shippingAddress'
										onChange={(e) => setShippingAddress(e.target.value)}
									/>
									<CardElement id='card-element' options={cardStyle} onChange={handleChange} />
									<Button onClick={placeOrder}>Place Order</Button>
									{error && (
										<div className='card-error' role='alert'>
											{error}
										</div>
									)}
									<p className={succeeded ? 'result-message' : 'result-message hidden'}>
										Payment succeeded, see the result in your
										<a href={`https://dashboard.stripe.com/test/payments`}>
											{' '}
											Stripe dashboard.
										</a>{' '}
										Refresh the page to pay again.
									</p>
								</Fragment>
							) : (
								<p>Sign in or Sign up to place an order</p>
							)}
						</div>
					</Col>
				</Row>
			)}

			{cart.length <= 0 && <p>You have nothing in the cart.</p>}
		</Fragment>
	);
};

export default CartItems;
