import React, { Fragment, useContext } from 'react';
import { Button } from 'reactstrap';

import { AppContext } from '../lib/AppContext';
import { removeProductFromCart } from '../lib/utils';

import gql from 'graphql-tag';
import { useMutation } from '@apollo/react-hooks';

const PLACE_ORDER = gql`
	mutation createOrders($type: createOrdersInput!) {
		createOrders(input: $type) {
			order {
				id
			}
		}
	}
`;

const CartItems = () => {
	const { globalStore, updateStore } = useContext(AppContext);
	const [createOrders] = useMutation(PLACE_ORDER);

	const { user, cart } = globalStore;

	const totalPrice = cart.reduce((a, c) => {
		return a + c.price;
	}, 0);

	const placeOrder = () => {
		const productIds = cart.reduce((a, c) => {
			a.push(c.id);
			return a;
		}, []);
		createOrders({ variables: { type: { data: { user: user._id, products: productIds } } } });
	};
	console.log(user);
	const removeItem = (cart, id) => {
		cart = removeProductFromCart(cart, id);
		updateStore('cart', cart);
		localStorage.setItem('cart', JSON.stringify(cart));
	};

	return (
		<Fragment>
			{cart.length > 0 && (
				<Fragment>
					{cart.map((product, i) => {
						return (
							<div key={i}>
								<h4>{product.name}</h4>
								<p>{product.description}</p>
								<p>{product.price}</p>
								<Button onClick={() => removeItem(cart, product.id)}>Remove From Cart</Button>
							</div>
						);
					})}
					<p>Total Price:{totalPrice}</p>
					<Button onClick={placeOrder}>Place Order</Button>
				</Fragment>
			)}
			{cart.length <= 0 && <p>You have nothing in the cart.</p>}
		</Fragment>
	);
};

export default CartItems;
