import React from 'react';
import { Card, CardImg, CardText, CardBody, CardTitle, CardSubtitle, Button, Row, Col } from 'reactstrap';

import gql from 'graphql-tag';
import { useMutation } from '@apollo/react-hooks';

const UPDATE_CART = gql`
	mutation updateCart($type: updateCartInput!) {
		updateCart(input: $type) {
			cart {
				id
			}
		}
	}
`;

const ProductsList = ({ products, user }) => {
	const [updateCart, { data }] = useMutation(UPDATE_CART);
	console.log(user);
	const addProductToCard = (product) => {
		updateCart({ variables: { type: { data: { products: [product.id], user: user._id } } } });
	};

	return (
		<Row>
			{products.map((product, i) => {
				return (
					<Col key={i}>
						<Card className='p-2'>
							<Row>
								<Col md={3}>
									<div className='product-image'>
										<CardImg
											top
											width='100%'
											height='100%'
											src={`http://localhost:1337${product.displayImage.url}`}
											alt='Card image cap'
										/>
									</div>
								</Col>
								<Col>
									<Row>
										<Col>
											<CardTitle>{product.name}</CardTitle>
										</Col>
									</Row>
									<Row>
										<Col>
											<CardSubtitle>{product.price}</CardSubtitle>
										</Col>
									</Row>
									<Row>
										<Col>
											<Button onClick={() => addProductToCard(product)}>Add To Cart</Button>
										</Col>
									</Row>
								</Col>
							</Row>
						</Card>
					</Col>
				);
			})}
		</Row>
	);
};

export default ProductsList;
