import withAuth from '../lib/withAuth';
import withLayout from '../lib/withLayout';

import { useQuery } from '@apollo/react-hooks';
import gql from 'graphql-tag';

const FETCH_ORDER = gql`
	query orders($where: JSON) {
		orders(where: $where) {
			products {
				name
				price
			}
		}
	}
`;

const Orders = ({ user }) => {
	const { loading, error, data } = useQuery(FETCH_ORDER, {
		variables: { $type: { user: { id: user.id } } },
	});
	if (loading) return <p>Loading ...</p>;
	return (
		<div>
			{data.orders.map((order) => {
				return (
					<div>
						<h4>{order.id}</h4>
						{order.products.map((product) => {
							return (
								<div>
									<p>{product.name}</p>
									<p>{product.price}</p>
								</div>
							);
						})}
					</div>
				);
			})}
		</div>
	);
	return <h1>Address {data.orders[0].shippingAddress}!</h1>;
};

export default withAuth(withLayout(Orders), { loginRequired: true });
