import React, { useEffect, useState, Fragments } from 'react';
import withAuth from '../lib/withAuth';
import withLayout from '../lib/withLayout';

const Cart = ({ user }) => {
	const [cartItems, setCartItems] = useState();

	useEffect(() => {
		let localCart = localStorage.getItems('cart');
		localCart = localCart.filter((item) => {
			return item.userId === user._id;
		});
		setCartItems(localCart);
	}, [cartItems]);

	return (
		<Fragments>
			<h2>Items in Your Cart</h2>
			{cartItems.map((product) => {
				return <h4>{product.name}</h4>;
			})}
		</Fragments>
	);
};

export default withAuth(withLayout(Cart), { loginRequired: true });
