import React from 'react';
import { Card, CardImg, CardText, CardBody, CardTitle, CardSubtitle, Button, Row, Col } from 'reactstrap';

import gql from 'graphql-tag';
import { useMutation } from '@apollo/react-hooks';

const CREATE_CART = gql`
	mutation CreateCart($type: createCartInput!) {
		createCart(input: $type) {
			cart {
				id
			}
		}
	}
`;

const ProductsList = ({ products, user }) => {
	const [createCart, { data }] = useMutation(CREATE_CART);
	const addProductToCard = (product) => {
		const input = { products: [product], user: user };
		createCart({ variables: { type: input } });
	};

	return (
		<Row>
			{products.map((product, i) => {
				return (
					<Col key={i}>
						<Card className='p-2'>
							<Row>
								<Col md={3}>
									<div className='product-image'>
										<CardImg
											top
											width='100%'
											height='100%'
											src={`http://localhost:1337${product.displayImage.url}`}
											alt='Card image cap'
										/>
									</div>
								</Col>
								<Col>
									<Row>
										<Col>
											<CardTitle>{product.name}</CardTitle>
										</Col>
									</Row>
									<Row>
										<Col>
											<CardSubtitle>{product.price}</CardSubtitle>
										</Col>
									</Row>
									<Row>
										<Col>
											<Button onClick={() => addProductToCard(product)}>Add To Cart</Button>
										</Col>
									</Row>
								</Col>
							</Row>
						</Card>
					</Col>
				);
			})}
		</Row>
	);
};

export default ProductsList;
