/* global fetch */

import { all, call, delay, put, take, takeLatest } from 'redux-saga/effects';
import es6promise from 'es6-promise';
import 'isomorphic-unfetch';

import { actionTypes, loadUser, loadProducts } from './actions';

es6promise.polyfill();

function* loadProductSaga() {
	try {
		const res = yield fetch('http://localhost:1337/products');
		const data = yield res.json();
		yield put(loadProducts(data));
	} catch (err) {
		console.log(err);
		//yield put(failure(err))
	}
}

function* loadUserSaga() {
	try {
		const res = yield fetch('http://localhost:1337/users/me/');
		const data = yield res.json();
		yield put(loadUser(data));
	} catch (err) {
		//yield put(failure(err))
	}
}

function* rootSaga() {
	yield all([takeLatest(actionTypes.LOAD_PRODUCTS, loadProductSaga)]);
	//takeLatest(actionTypes.LOAD_USER, loadUserSaga),
}

export default rootSaga;
