import ProductsList from '../components/ProductsList';
import cookies from 'next-cookies';

export default ({ products }) => {
	return (
		<div>
			<h2>List</h2>
			<ProductsList products={products} />
		</div>
	);
};

// This function gets called at build time on server-side.
