import React from 'react';
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';

const Signup = (props) => {
	const name = useRef();
	const password = useRef();
	const passwordSecond = useRef();
	const email = useRef();

	const onFormSubmit = () => {
		console.log(name);
	};

	return (
		<Form>
			<FormGroup>
				<Label for='name'>Name</Label>
				<Input type='text' name='name' id='name' ref={name} placeholder='Enter Name' />
			</FormGroup>
			<FormGroup>
				<Label for='password1'>Password</Label>
				<Input type='password' name='password' id='password1' ref={password} placeholder='Enter Password' />
			</FormGroup>
			<FormGroup>
				<Label for='password2'>Re-enter Password</Label>
				<Input
					type='password'
					name='password2'
					id='password2'
					ref={passwordSecond}
					placeholder='Enter Password Again'
				/>
			</FormGroup>
			<FormGroup>
				<Label for='Email'>Email</Label>
				<Input type='email' name='email' id='email' ref={email} placeholder='Enter Email' />
			</FormGroup>
			<Button>Submit</Button>
		</Form>
	);
};

export default Signup;
