export const actionTypes = {
	LOAD_USER: 'LOAD_USER',
	LOAD_PRODUCTS: 'LOAD_PRODUCTS',
};

export function loadUser() {
	return { type: actionTypes.LOAD_USER };
}

export function loadProducts() {
	return { type: actionTypes.LOAD_PRODUCTS };
}
